import React, { useRef, useState } from "react";
import Footer from "../../components/Footer/Footer";
import MealsCard from "../../components/MealsCard/MealsCard";
import Navbar from "../../components/Navbar/Navbar";
import axios from "axios";
import "./listOfMeals.css";

const ListOfMeals = () => {
  const [fetchedData, setFetchedData] = useState(null);
  const ingridient = useRef(null);

  async function getData() {
    const options = {
      method: "GET",
      url: "https://yummly2.p.rapidapi.com/feeds/auto-complete",
      params: { q: ingridient },
      headers: {
        "X-RapidAPI-Host": "yummly2.p.rapidapi.com",
        "X-RapidAPI-Key": "ead1815035msh64c02657aaa8896p1fb548jsn31715607626a",
      },
    };

    const responseData = await axios
      .request(options)
      .then(responseData => responseData.data.searches)
      .catch(error => error);

    setFetchedData(responseData);
  }

  console.log(fetchedData);

  return (
    <div className="mealsList">
      <Navbar />
      <div className="mealsList-content">
        <div className="mealsList-title">List of meals</div>

        <div className="mealsList-search">
          <input
            type="text"
            placeholder="Enter ingridient of meal"
            ref={ingridient.current}
          />
          <button onClick={getData}>Find</button>
        </div>

        <div className="mealsList-list"></div>
      </div>
      <Footer />
    </div>
  );
};

export default ListOfMeals;
