import { useCookies } from "react-cookie";
import jwt from "jsonwebtoken";

const useUserData = () => {
  const [cookies] = useCookies();
  const token = cookies.userAuthData.token || null;

  if (token) {
    const data = jwt.decode(token);
    console.log(data);
  }

  return true;
};

export default useUserData;
