import React, { useRef } from "react";
import "./createAdvice.css";
import { ref } from "@firebase/storage";
import { storage } from "../../../firebase/firebase.config";

const CreateAdviceFields = () => {
  const imageFileRef = useRef();


  function uploadFiles() {
    const fileValue = imageFileRef.target.value;
    const storageRef = ref(storage, `/adviceImages/${fileValue.name}`);
    if (!fileValue) return alert("No file uploaded");
  }
  return (
    <div>
      <h1>Create new advice</h1>
      <input type="file" ref={imageFileRef} placeholder="upload" />
    </div>
  );
};

export default CreateAdviceFields;
