import React, { useRef } from "react";
import "./createAdvice.css";

const CreateAdviceFields = () => {
  const imageFileRef = useRef();

  async function uploadImage() {
    const fileValue = imageFileRef.target.value;
    const url = await uploadAndGetURL(fileValue);
    console.log(url);
  }

  return (
    <div>
      <h1>Create new advice</h1>
      <input type="file" ref={imageFileRef} placeholder="upload" />
      <button onClick={uploadImage}>Upload</button>
    </div>
  );
};

export default CreateAdviceFields;
