import React from "react";

const useAdjustDate = rawDate => {
  const adjustedDate = Date(rawDate);
  const yyyy = today.getFullYear();
  let mm = today.getMonth() + 1; // Months start at 0!
  let dd = today.getDate();

  return adjustedDate;
};

export { useAdjustDate };
