import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";

const useAuth = () => {
  const [cookies] = useCookies([process.env.REACT_APP_USER_COOKIES]);
  const isAuth = cookies?.isAuth;

  return isAuth;
};

export default useAuth;
