import React from "react";
import { useState } from "react";
import "./noteItem.css";

const NoteItem = ({ title, body }) => {
  const [isVisible, setIsVisible] = useState(false);

  function deleteBtnVisibility() {
    return isVisible ? "isActiveDeleteBtn" : "noteItem-delete";
  }

  return (
    <div className="noteItem" onMouseOver={() => setIsVisible(!isVisible)}>
      <div className="noteItem-title">{title}</div>
      <div className="noteItem-gap">:</div>
      <div className="noteItem-body">{body}</div>

      <div className={deleteBtnVisibility()}>
        <button>Delete</button>
      </div>
    </div>
  );
};

export default NoteItem;
