import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";

const useAuth = () => {
  const [cookies] = useCookies();
  const userAuthCookiesName = process.env.REACT_APP_USER_COOKIES;
  const isAuth = cookies.userAuthCookiesName?.isAuth;

  console.log(isAuth);

  return isAuth;
};

export default useAuth;
