import React from "react";
import { Link, useParams } from "react-router-dom";
import { useFetchNotesList } from "../../Hooks/useFetchNotesList";
import EventInfoItem from "../EventInfoItem/EventInfoItem";

const NotesFields = () => {
  const { id } = useParams();
  const notesList = useFetchNotesList(id);
  const url = `/create-note/${id}`;

  return (
    <div className="card-notes card">
      <div className="card-title">Event notes</div>
      <div className="card-list">
        {notesList &&
          notesList.map(note => (
            <NoteI title={note.noteTitle} body={note.noteBody} />
          ))}
      </div>
      <Link to={url} className="card-link">
        Add new notes
      </Link>
    </div>
  );
};

export default NotesFields;
