import React from "react";

const useAdjustDate = rawDate => {
  const adjustedDate = Date(rawDate);
  const yyyy = adjustedDate.getFullYear();
  let mm = adjustedDate.getMonth() + 1;
  let dd = adjustedDate.getDate();

  return adjustedDate;
};

export { useAdjustDate };
