import React from "react";

const useAdjustDate = rawDate => {
  const date = Date(rawDate).split(" ");
  const adjustedDate = `${rawDate[1]} - ${rawDate[2]} - ${rawDate[3]}`;

  return date;
};

export { useAdjustDate };
